/*global jQuery, Galleria */

Galleria.requires(1.25, 'This version of Classic theme requires Galleria 1.2.5 or later');

(function($) {

Galleria.addTheme({
    name: 'uplo',
    author: 'Galleria',
    css: 'galleria.uplo.css',
    defaults: {
        transition: 'slide',
        thumbCrop:  'height',

        // set this to false if you want to show the caption all the time:
        _toggleInfo: true
    },
    init: function(options) {
        var opacity = 0.6;
        // add some elements
        this.addElement('info-link','info-close');
        this.append({
            'info' : ['info-link','info-close']
        });

        // cache some stuff
        var info = this.$('info-link,info-close,info-text'),
            touch = Galleria.TOUCH,
            click = touch ? 'touchstart' : 'click';

        // show loader & counter with opacity
        this.$('loader,counter').show().css('opacity', 0.2);

        // Hide image description
        this.remove('info-description');
        
        this.$('thumbnails').css({height: "84px"});
        // some stuff for non-touch browsers
        if (! touch ) {
            this.addIdleState( this.get('image-nav-left'), { left:-50 });
            this.addIdleState( this.get('image-nav-right'), { right:-50 });
            this.addIdleState( this.get('counter'), { opacity:0 });
        }

        // toggle info
        if ( options._toggleInfo === true ) {
            info.bind( click, function() {
                info.toggle(400);
            });
        } else {
            info.fadeIn(400)
            this.$('info-link, info-close').fadeOut(400);
        }

        // bind some stuff
        this.bind('thumbnail', function(e) {

            if (! touch ) {
                // fade thumbnails
                $(e.thumbTarget).parent().hover(function() {
                    $(this).not('.active').children().stop().fadeTo(100, 1);
                }, function() {
                    $(this).not('.active').children().stop().fadeTo(400, opacity);
                });

                if ( e.index === this.getIndex() ) {
                    $(e.thumbTarget).parent().addClass("current");
                }
            } else {
                if(this.getIndex()){
                    $(e.thumbTarget).parent().addClass("current");
                }
                else{
                    $(e.thumbTarget).parent().removeClass("current");
                }
                
            }
        });

        this.bind('loadstart', function(e) {
            if (!e.cached) {
                this.$('loader').show().fadeTo(200, opacity);
                // Show the thumbnail.
                console.log("wait for loading the image");
                
                var stage = this.$('stage');
                var found = false;
                $(stage).find(".galleria-image").each(function(id, elm){
                    if(!found && $(elm).css("opacity") == "1"){
                        found = true;
                        var img = $(elm).find("img");
                        if(!img || img.length <= 0){
                          console.log("append the thumbnail");
                          $(elm).append($(e.thumbTarget).clone());
                        }
                        else{
                            console.log("change the image src");
                           $(img).attr("src", $(e.thumbTarget).attr("src"));
                        }
                        
                        return;
                    }
                });
            }
            
            //console.log(">> start load");
            //console.log(">>> imageTarget");
            //console.log(e.imageTarget);
            //console.log(">>> thumbTarget");
            //console.log(e.thumbTarget);
            //var width = $(e.thumbTarget).width();
            //var height = $(e.thumbTarget).height();
            //console.log("width:" + width);
            //console.log("height:" + height);
            //
            //$(e.imageTarget).attr("src", $(e.thumbTarget).attr("src"));
            //$(e.imageTarget).attr("class", "_test_class");
            //console.log($(e.imageTarget));
            
            
            this.$('info').toggle( this.hasInfo() );
            var parent = $(e.thumbTarget).css('opacity', 1).parent().addClass("current");
            $(parent).siblings().removeClass("current");
        });

        this.bind('loadfinish', function(e) {
            this.$('loader').fadeOut(200);
            // Hide the thumbnail
            console.log(">> finish load");
        });
    }
});

}(jQuery));
